mapper.select:
select *
from orders
where user_id = #{userId}

    and status = #{status}

order by order_time desc

select o.*,
GROUP_CONCAT(
CONCAT(od.name, '*', od.number)
SEPARATOR ',' ) as orderDishes
from orders o left join order_detail od on o.id = od.order_id


        and o.number like concat('%', #{number}, '%')


        and o.phone like concat('%', #{phone}, '%')


        and o.status = #{status}


        and o.user_id = #{userId}


        and o.order_time >= #{beginTime}


        and o.order_time <= #{endTime}


group by o.id, o.order_time
order by o.order_time desc

select count(*)
from orders
where status = #{status}
